import React from "react"  ;
import { useState, useEffect } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

function Login() {
  const [userPass, setBook] = useState({  Username: "", Password: "" });


  const navigate = useNavigate();

  const handleChange = (e) =>{
    setBook((prev)=>({...userPass, [e.target.name]: e.target.value}));
  }

  const handleClick = async () =>{ 
    try{    
      console.log("User Pass ", userPass);
      const res = await axios.post("http://localhost:5000/login", userPass);
      console.log("USer Password is ",res);
      navigate("/books");
    }catch(error){
      console.log(error);
    }
  }

  return (
    <div>
      <h1>Login</h1>
      <input type="text" placeholder="Username"  name="username" onChange={handleChange}></input>
      <input type="password" placeholder="Password"  name="password" onChange={handleChange}></input>
      <button className="loginButton" onClick={handleClick}><Link to={'/books'}>Login</Link></button>
    </div>
  );
}

export default Login;
