import React from "react"  ;
import { useState } from "react";
import axios from "axios";
import { Link, useNavigate} from "react-router-dom";

function Login() {
  const [userPass, setBook] = useState({  UserName: "", Password: "" });


  const handleChange = (e) =>{
    setBook((prev)=>({...userPass, [e.target.name]: e.target.value}));
  }

  const navigate = useNavigate();

    const handleClick = async () =>{
    try{    
      console.log("Books ", userPass);
      const res = await axios.get("http://localhost:5000/login", userPass);
      console.log("The response from the frontend is this",res);
      navigate("/books");
    } catch(error){
      console.log(error);
    }
  }

  return (
    <div>
      <h1>Login</h1>
      <input type="text" placeholder="title" onChange={handleChange} name="title"></input>
      <input type="text" placeholder="conetnts" onChange={handleChange} name="contents"></input>

      <button className="btn" onClick={handleClick}><Link to={'/books'}>Add Note</Link></button>
    </div>
  );
}

export default Login;
