import React from "react"  ;
import { useState } from "react";
import axios from "axios";
import { Link, useNavigate} from "react-router-dom";

function Login() {
  const [userPass, setBook] = useState({  Username: "", Password: "" });


  const handleChange = (e) =>{
    setBook((prev)=>({...userPass, [e.target.name]: e.target.value}));
    console.log(userPass);
  }

  const navigate = useNavigate();

    const handleClick = async () =>{
    try{    
      console.log("Books ", userPass);
      const res = await axios.get("http://localhost:5000/login", userPass);
      console.log("The response from the server is this",res);
      navigate("/books");
    } catch(error){
      console.log(error);
    }
  }

  return (
    <div>
      <h1>Login</h1>
      <input type="text" placeholder="Username"  name="UserName" onChange={handleChange}></input>
      <input type="password" placeholder="Password"  name="Password" onChange={handleChange}></input>
      <button className="loginButton" onClick={handleClick} >Login</button>
    </div>
  );
}

export default Login;
